import { useState } from 'react'
import axios from 'axios'


const [nome, setNome] = useState('');
const [categoria, setCategoria] = useState('');
const [valor, setValor] = useState('');
const [desc, setDesc] = useState('');
const [estoque, setEstoque] = useState('');





function RegistrarProduto (){

  return (
    <div className="RegistroProduto">
    <h1 className="Adicionar-RP">Registre seu produto</h1>
        <div id="row-RP" className="container ">
            <div className="Border-RP row m-2">
                <div>                      
                    <form className="row" onSubmit={this.handleSubmit}>
                      <div className="col-6">
                      <label>
                      <br />
                      <h4 className="RP-Form m-3">Nome do produto:</h4>
                      <div class="input-group px-3">
                          <input type="text" aria-label="First name" onChange={e=>setNome(e.target.value)} className="form-control" />
                      </div>                     
                      </label>
                      <label>
                      <h4 className="RP-Form m-3">Preço do produto:</h4>
                      <div class="input-group px-3">
                          <input type="number" aria-label="First name" class="form-control" />
                      </div>          
                      </label>
                      <label>
                      <h4 className="RP-Form m-3">Descrição do produto:</h4>
                      <div class="input-group px-3">
                          <input type="text" aria-label="First name" class="form-control" />
                      </div>      
                      </label>
                      </div>

                      <div className="col-6">
                          <h3 className="RP-Form mt-5">Opções de entrega:</h3>
                          <div class="input-group mx-2">
                            <div class="input-group-text">
                              <input class="form-check-input mt-0" type="radio" value="" aria-label="Radio button for following text input" />
                            </div>
                            <input type="text" class="form-control" aria-label="Text input with radio button" />
                          </div>
                          <h4 className="RP-Form"><input type="checkbox"  onChange={this.handleChange} />
                          Delivery</h4>
                          <h4 className="RP-Form"><input type="checkbox"  onChange={this.handleChange} />
                          Mercados parceiros</h4>
                      </div>
                        <div>
                          <h3 className="RP-Form">Fotos do produto:</h3>
                          <h4 className="RP-Form">Fotos principais <br /></h4>
                          <div class="input-group mb-3 px-4">
                            <input type="file" class="form-control" id="inputGroupFile02" />
                          </div>
                          <h4 className="RP-Form">Fotos auxiliares <br /></h4>
                          <div class="input-group mb-3 px-4">
                            <input type="file" class="form-control" id="inputGroupFile02" />
                          </div>                          
                      </div>
                    </form>
                </div>                    
            </div>
        </div>
    </div> 
  );
}








